// Calculate centered offsets
localparam H_OFFSET = 192;
localparam V_OFFSET = 176;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        red_reg <= 4'b0000;
        green_reg <= 4'b0000;
        blue_reg <= 4'b0000;
    end else begin
        if (pixel_x >= H_OFFSET && pixel_x < H_OFFSET + 256 &&
            pixel_y >= V_OFFSET && pixel_y < V_OFFSET + 128) begin
            // Fetch the correct pixel from the memory
            if (main[pixel_y - V_OFFSET][255 - (pixel_x - H_OFFSET)]) begin
                red_reg <= 4'b1111;
                green_reg <= 4'b0000; // Red output
                blue_reg <= 4'b0000;
            end else begin
                red_reg <= 4'b0000;
                green_reg <= 4'b0000;
                blue_reg <= 4'b0000;
            end
        end else begin
            red_reg <= 4'b0000;
            green_reg <= 4'b0000;
            blue_reg <= 4'b0000;
        end
    end
end

